package Lecture1;

public class Opertaors {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		a+=2; //a= a+2
		a-=2; // a = a-2
		a*=2; // a= a*2;
		a/=2; // a= a/2
		
		//Below 3 are same
		a = a+1;
		a+=1;
		a++;
		
		// Below 3 are same
		a = a-1;
		a-=1;
		a--;
		
		
		
		
	}

}
